document.querySelectorAll("[type='password']").forEach((element) => {
  element.classList.add("input-password");
});
document.querySelectorAll("#toggle-password").forEach((element, index) => {
  element.classList.remove("d-none");
});
const passwordInput = document.querySelectorAll("[type='password']");
const togglePasswordButton = document.querySelectorAll("#toggle-password");
document.querySelectorAll("#toggle-password").forEach((element, index) => {
  togglePasswordButton[index].addEventListener("click", () => {
    togglePassword(index);
  });
});
function togglePassword(index) {
  if (passwordInput[index].type === "password") {
    passwordInput[index].type = "text";
    togglePasswordButton[index].setAttribute("aria-label", "Hide password.");
  } else {
    passwordInput[index].type = "password";
    togglePasswordButton[index].setAttribute(
      "aria-label",
      "Show password as plain text. " +
        "Warning: this will display your password on the screen."
    );
  }
}
